import{_ as n,o as e,c as a,R as t}from"./chunks/framework.EUU6lLMq.js";const _=JSON.parse('{"title":"KernelSU là gì?","description":"","frontmatter":{},"headers":[],"relativePath":"vi_VN/guide/what-is-kernelsu.md","filePath":"vi_VN/guide/what-is-kernelsu.md"}'),h={name:"vi_VN/guide/what-is-kernelsu.md"},r=t('<h1 id="kernelsu-la-gi" tabindex="-1">KernelSU là gì? <a class="header-anchor" href="#kernelsu-la-gi" aria-label="Permalink to &quot;KernelSU là gì?&quot;">​</a></h1><p>KernelSU là một giải pháp root cho các thiết bị Android GKI, nó hoạt động ở chế độ kernel và cấp quyền root cho ứng dụng không gian người dùng trực tiếp trong không gian kernel.</p><h2 id="tinh-nang" tabindex="-1">Tính năng <a class="header-anchor" href="#tinh-nang" aria-label="Permalink to &quot;Tính năng&quot;">​</a></h2><p>Tính năng chính của KernelSU là <strong>Kernel-based</strong> (dựa trên Kernel). KernelSU hoạt động ở chế độ kernel nên nó có thể cung cấp giao diện kernel mà chúng ta chưa từng có trước đây. Ví dụ: chúng ta có thể thêm điểm dừng phần cứng vào bất kỳ quy trình nào ở chế độ kernel; Chúng ta có thể truy cập bộ nhớ vật lý của bất kỳ quy trình nào mà không bị phát hiện; Chúng ta còn có thể chặn bất kỳ syscall nào trong không gian kernel; v.v.</p><p>Ngoài ra, KernelSU còn cung cấp hệ thống mô-đun thông qua lớp phủ, cho phép bạn tải plugin tùy chỉnh vào hệ thống. Nó cũng cung cấp một cơ chế để sửa đổi các tập tin trong phân vùng <code>/system</code>.</p><h2 id="huong-dan-su-dung" tabindex="-1">Hướng dẫn sử dụng <a class="header-anchor" href="#huong-dan-su-dung" aria-label="Permalink to &quot;Hướng dẫn sử dụng&quot;">​</a></h2><p>Xin hãy xem: <a href="./installation.html">Cách cài đặt</a></p><h2 id="cach-đe-build" tabindex="-1">Cách để build <a class="header-anchor" href="#cach-đe-build" aria-label="Permalink to &quot;Cách để build&quot;">​</a></h2><p><a href="./how-to-build.html">Cách để build</a></p><h2 id="thao-luan" tabindex="-1">Thảo luận <a class="header-anchor" href="#thao-luan" aria-label="Permalink to &quot;Thảo luận&quot;">​</a></h2><ul><li>Telegram: <a href="https://t.me/KernelSU" target="_blank" rel="noreferrer">@KernelSU</a></li></ul>',11),l=[r];function c(i,o,g,d,s,u){return e(),a("div",null,l)}const m=n(h,[["render",c]]);export{_ as __pageData,m as default};
